% para o for, repetir una o mas instruciones,una cantidad de veces definida% En la variable definida se guarda el numero por el que se va for i = 1:10   disp(i)  %Instruccionesend% Mostrar numeros pares del 1 hasta el 100for numero = 1:100  if ( mod(numero,2) == 0)     disp(numero)  end
end% Decir si un numero es primo o no % Si un numero es disible solamente entre el y la unidad es primiro% Si encuentro un numero que no sea el mismo ni la unidad cuyo residuo sea cero% significa que el numero no es primonumero = 17 ; % buscar si es primoesPrimo = 1 ; % por defecto asumimos que es primofor i = 2:numero-1 % buscar si existe un numero que divida a la variable numero  if ( mod(numero, i) == 0)     esPrimo = 0;  end
endif(esPrimo == 1)  fprintf('El numero %d es primo\n' , numero);else   fprintf('El numero %d no es primo\n' , numero);end